import React, { Component } from "react";
import AliasedInput from "./aliasedInput.jsx";
import SkillInput from "./skillInput.jsx";

const SkillsGrid = ({
  skills,
  handleSkillTitleChange,
  handleSkillAliasChange,
  handleDelete,
  handleClear,
  numCols
}) => {
  return (
    <div className="container">
      <div className="row">
        <div className="col-lg">
          {" "}
          {Object.keys(skills).map((id, i) => {
            if (i % numCols != 0) {
              return;
            }
            return (
              <SkillInput
                id={id}
                skills={skills}
                handleTitleChange={handleSkillTitleChange}
                handleAliasChange={handleSkillAliasChange}
                handleDelete={handleDelete}
                handleClear={handleClear}
              />
            );
          })}
        </div>

        <div className="col-lg">
          {" "}
          {Object.keys(skills).map((id, i) => {
            if (i % numCols != 1) {
              return;
            }
            return (
              <SkillInput
                id={id}
                skills={skills}
                handleTitleChange={handleSkillTitleChange}
                handleAliasChange={handleSkillAliasChange}
                handleDelete={handleDelete}
                handleClear={handleClear}
              />
            );
          })}
        </div>

        <div className="col-lg">
          {" "}
          {Object.keys(skills).map((id, i) => {
            if (i % numCols != 2) {
              return;
            }
            return (
              <SkillInput
                id={id}
                skills={skills}
                handleTitleChange={handleSkillTitleChange}
                handleAliasChange={handleSkillAliasChange}
                handleDelete={handleDelete}
                handleClear={handleClear}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default SkillsGrid;
