import React, { Component } from "react";
import InputList from "./inputList.jsx";
import http from "../services/httpService";
import config from "../config.json";
import { toast } from "react-toastify";
import CsvInput from "./csvInput.jsx";
import ListOfCSVInputs from "./listOfCSVInputs";

class ScraperForm extends Component {
  state = {
    jobName: "",
    jobAliases: [],
    inputs: {},
    errors: {},
    titleLabel: "Skill",
    csvLabel: "Aliases"
  };

  componentDidMount() {
    const inputs = {
      id: { title: "", aliases: [] }
    };
    this.setState({ inputs });
  }

  handlePost = async e => {
    console.log("handle post triggered");
    e.preventDefault();

    const originalJob = this.state.job;

    try {
      await http.post(
        config.scraperServerAPIEndpoint + "/api/scraper",
        this.state.job
      );
    } catch (ex) {
      toast.error("conection error TODO: fix this message");
      this.setState({ job: originalJob });
    }
  };

  render() {
    const { jobName, jobAliases, inputs, csvLabel, titleLabel } = this.state;

    return (
      <div>
        <CsvInput
          title="a"
          // name="a"
          // aliases="a"
          // titleLabel="a"
          // csvLabel="a"
          // handleTitleChange="a"
          // handleAliasChange="a"
        />
        <ListOfCSVInputs
          // key={key}
          // name={key}
          // title={inputs[key].title}
          // aliases={inputs[key].aliases}
          // titleLabel={titleLabel}
          // csvLabel={csvLabel}
          // handleTitleChange={this.handleTitleChange}
          // handleAliasChange={this.handleAliasChange}
        />
      </div>
      // <div>
      //   <h3 className="mt-5">Job title</h3>
      //   <TitledInputList />
      //   <p></p>
      //   <p></p>

      //   <h3 className="mt-5">Skills</h3>
      //   <TitledInputList />
      //   <button onClick={this.handlePost} className="btn-lg btn-primary mt-5">
      //     Submit job
      //   </button>
      // </div>
    );
  }
}

export default ScraperForm;
