import React, { Component } from "react";
import Input from "./input.jsx";

class AliasedInput extends Component {
  state = {
    title: "",
    titleLabel: "",
    aliases: ["a", "b", "c"]
  };

  handleChange() {
      console.log("ay");
  }

  handleAliasChange({ currentTarget: input }) {
    // console.log(input);
    const aliases = {...this.state.aliases};
    aliases =  input.value.splot(',');
    this.setState( {aliases} );
  }

  render() {
    return (
      <div>
        <Input
          name="title"
          label={this.state.titleLable}
          value={this.state.title}
          onChange={this.handleChange}
          sizeClass={"form-control-lg"}
        />
          <Input
            label="Aliases"
            // name={key}
            value={this.state.aliases.join(", ")}
            onChange={this.handleAliasChange}
            sizeClass={"form-control-sm ml-5"}
          />
      </div>
    );
  }
}

export default AliasedInput;
