import React, { Component } from "react";
const shortid = require("shortid");

class InputList extends Component {
  state = {
    values: "react, node",
    newValue: "",
    visible: false
  };

  handleClick = () => {
    const visible = !this.state.visible;
    this.setState({ visible });
  };

  handleDelete = val => {
    let values = this.state.values.split(",");
    values = values.map(Function.prototype.call, String.prototype.trim);
    let index = values.indexOf(val.trim());
    if (index > -1) {
      values.splice(index, 1);
    }
    this.setState({ values: values.join(", ") });
  };

  handleChange = ({ currentTarget: input }) => {
    this.setState({ newValue: input.value });
  }

  handleCreate = () => {
    let values = this.state.values + ", " + this.state.newValue;
    this.setState({ values });
  };

  render() {
    return (
      <div>
        <button onClick={this.handleClick} className="btn btn-primary">
          Aliases
        </button>

        <ul className="list-group list-group-flush">
          {this.state.visible &&
            this.state.values.split(",").map(val => {
              return (
                <div className="input-group" key={shortid.generate()}>
                  <li className="list-group-item list-group-item-primary">
                    {val}
                  </li>
                  <button
                    onClick={() => {
                      this.handleDelete(val);
                    }}
                    className="btn btn-danger"
                    type="button"
                  >
                    -
                  </button>
                </div>
              );
            })}
        </ul>

        <div className="input-group mx-sm">
          <input
            type="text"
            className="form-control"
            placeholder="Input alias"
            value={this.state.newValue}
            onChange={this.handleChange}
            // aria-label="Recipient's username"
            // aria-describedby="basic-addon2"
          />
          <div className="input-group-append">
            <button
              onClick={() => {
                this.handleCreate();
              }}
              className="btn btn-success"
              type="button"
            >
              Button
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default InputList;
