import React, { Component } from "react";

const Input = ({ name, label, value, onChange, sizeClass }) => {
  return (
    <div className="form-group">
      { label!="" ? <label htmlFor={name}>{label}</label> : 0}
      <input
        value={value}
        onChange={onChange}
        id={name}
        type="text"
        className={ "form-control " + sizeClass }
      />
    </div>
  );
};

export default Input;
