import React, { Component } from "react";
import Popup from "reactjs-popup";
const shortid = require("shortid");

class InputList extends Component {
  state = {
    values: "react,node",
    newValue: "",
    name: "",
    id: ""
  };

  componentDidMount() {
    const { id, name } = this.props;
    this.setState({ name: name, id: id });
  }

    handleDelete = val => {
      let values = this.props.values.split(",");
      values = values.map(Function.prototype.call, String.prototype.trim);
      let index = values.indexOf(val.trim());
      if (index > -1) {
        values.splice(index, 1);
      }
      this.setState({ values: values.join(",") });
      console.log(values);
    };


  handleChange = ({ currentTarget: input }) => {
    this.setState({ newValue: input.value });
  };

  handleCreate = e => {
    const { name, id, newValue } = this.state;
    const { values, helperFunctions } = this.props;
    // Use callback from parent to update state...to do that, need name and possible id
    let newValues =
      this.props.newValues == ""
        ? newValue.trim()
        : this.props.newValues + "," + newValue.trim();

    let obj = { currentTarget: { name: name, value: newValues } };
    helperFunctions.handleChange(obj, id);
    this.setState({ newValue: "" });
  };

  onKeyPress = event => {
    if (event.key === "Enter") {
      this.handleCreate();
    }
  };

  render() {
    const { values, helperFunctions } = this.props;
    return (
      <div>
        <Popup
          trigger={
            <button onClick={this.handleClick} className="btn btn-primary menu">
              Aliases
            </button>
          }
          position="bottom left"
          offsetX={0}
          offsetY={1}
          on="hover"
          closeOnDocumentClick
          mouseLeaveDelay={300}
          mouseEnterDelay={0}
          lockScroll={true}
          contentStyle={{ padding: "0px", border: "none" }}
          arrow={false}
        >
          <div className="menu aliases">
            {values.split(",").map(val => {
              if (val.trim() === "") {
                return null;
              }
              return (
                <div className="input-group-prepend" key={shortid.generate()}>
                  <button
                    onClick={() => {
                      helperFunctions.handleDelete(val);
                    }}
                    className="btn-sm btn-danger"
                    type="button"
                  >
                    -
                  </button>
                  <span
                    aria-describedby="inputGroup-sizing-sm"
                    className="list-group-item list-group-item-primary"
                  >
                    {val}
                  </span>
                </div>
              );
            })}
            <div className="input-group mx-sm">
              <input
                type="text"
                className="form-control"
                placeholder="Input alias"
                value={this.state.newValue}
                onChange={this.handleChange}
                onKeyPress={this.onKeyPress}
              />
              <div className="input-group-append">
                <button
                  onClick={e => this.handleCreate(e)}
                  className="btn btn-success"
                  type="button"
                >
                  Add alias
                </button>
              </div>
              <div className="input-group aliases">
                <button
                  //   onClick={this.handleClear}
                  className="btn-sm btn-warning aliases"
                  type="button"
                >
                  Clear
                </button>
              </div>
            </div>
          </div>
        </Popup>
      </div>
    );
  }
}

export default InputList;
