import React, { Component } from "react";
import Input from "./input.jsx";

class InputList extends Component {
  state = {
    title: "spark",
    aliases: {
      0: "apachespark",
      1: "apache spark"
    }
  };

  handleChange = ({ currentTarget: input }) => {
    const aliases = { ...this.state.aliases };
    console.log(input);
    // console.log(aliases);
    // aliases[input] = ;
    // console.log(aliases);
    // this.setState({ aliases });
  };

  render() {
    const curState = this.state;

    return (
      <div>
        <Input
          name="title"
          label="title"
          value={curState.title}
          onChange={this.handleChange}
        />
        <div>
          {Object.keys(curState.aliases).map(key => (
            <Input
              key={key}
              name={key}
              label="alias"
              value={curState[key]}
              onChange={this.handleChange}
            />
          ))}
        </div>
        <button className="btn btn-primary">Add alias</button>
      </div>
    );
  }
}

export default InputList;
