import React, { Component } from "react";

const Input = ({ name, label, value }) => {
  return (
    <div className="form-group">
      <label htmlFor={name}>{label}</label>
      <input
        value={value}
        // onChange={this.handleChange}
        id={name}
        type="text"
        className="form-control"
      />
    </div>
  );
};

export default Input;
