import React, { Component } from "react";
import Popup from "reactjs-popup";
const shortid = require("shortid");

class InputList extends Component {
  state = {
    values: "react,node",
    newValue: "",
    visible: false
  };

  handleClick = () => {
    const visible = !this.state.visible;
    this.setState({ visible });
  };

  handleDelete = val => {
    let values = this.state.values.split(",");
    values = values.map(Function.prototype.call, String.prototype.trim);
    let index = values.indexOf(val.trim());
    if (index > -1) {
      values.splice(index, 1);
    }
    this.setState({ values: values.join(",") });
    console.log(values);
  };

  handleChange = ({ currentTarget: input }) => {
    this.setState({ newValue: input.value });
  };

  handleCreate = () => {
    let values =
      this.state.values == ""
        ? this.state.newValue.trim()
        : this.state.values + "," + this.state.newValue.trim();
    this.setState({ values: values, newValue: "" });
    console.log(values);
  };

  handleClear = () => {
    this.setState({ values: "" });
  };

  onKeyPress = event => {
    if (event.key === "Enter") {
      this.handleCreate();
    }
  };

  render() {
    return (
      <div>
        <Popup
          trigger={
            <button onClick={this.handleClick} className="btn btn-primary menu">
              Aliases
            </button>
          }
          position="bottom left"
          offsetX={0}
          offsetY={1}
          on="hover"
          closeOnDocumentClick
          mouseLeaveDelay={500}
          mouseEnterDelay={0}
          lockScroll={true}
          contentStyle={{ padding: "0px", border: "none" }}
          arrow={false}
        >
          <div className="menu aliases">
            {this.state.values.split(",").map(val => {
              if (val.trim() === "") {
                return null;
              }
              return (
                <div className="input-group-prepend" key={shortid.generate()}>
                  <button
                    onClick={() => {
                      this.handleDelete(val);
                    }}
                    className="btn-sm btn-danger"
                    type="button"
                  >
                    -
                  </button>
                  <span
                    aria-describedby="inputGroup-sizing-sm"
                    className="list-group-item list-group-item-primary"
                  >
                    {val}
                  </span>
                </div>
              );
            })}
            {/* </div> */}
            <div className="input-group mx-sm">
              <input
                type="text"
                className="form-control"
                placeholder="Input alias"
                value={this.state.newValue}
                onChange={this.handleChange}
                onKeyPress={this.onKeyPress}
              />
              <div className="input-group-append">
                <button
                  onClick={this.handleCreate}
                  className="btn btn-success"
                  type="button"
                >
                  Add alias
                </button>
              </div>
              <div className="input-group aliases">
                <button
                  onClick={this.handleClear}
                  className="btn-sm btn-warning aliases"
                  type="button"
                >
                  Clear
                </button>
              </div>
            </div>
            {/* </ul> */}
          </div>
        </Popup>

        {/* <button onClick={this.handleClick} className="btn btn-primary">
          Aliases
        </button>

        {this.state.visible && (
          <ul className="list-group list-group-flush">
            {this.state.visible &&
              this.state.values.split(",").map(val => {
                return (
                  <div className="input-group" key={shortid.generate()}>
                    <li className="list-group-item list-group-item-primary">
                      {val}
                    </li>
                    <button
                      onClick={() => {
                        this.handleDelete(val);
                      }}
                      className="btn btn-danger"
                      type="button"
                    >
                      -
                    </button>
                  </div>
                );
              })}
          </ul>
        )}
        {this.state.visible && (
          <div className="input-group mx-sm">
            <input
              type="text"
              className="form-control"
              placeholder="Input alias"
              value={this.state.newValue}
              onChange={this.handleChange}
              // aria-label="Recipient's username"
              // aria-describedby="basic-addon2"
            />
            <div className="input-group-append">
              <button
                onClick={this.handleCreate}
                className="btn btn-success"
                type="button"
              >
                Button
              </button>
            </div>
          </div>
        )} */}
      </div>
    );
  }
}

export default InputList;
