import React, { Component } from "react";
import Popup from "reactjs-popup";
const shortid = require("shortid");

class InputList extends Component {
  state = {
    values: "react, node",
    newValue: "",
    visible: false
  };

  handleClick = () => {
    const visible = !this.state.visible;
    this.setState({ visible });
  };

  handleDelete = val => {
    let values = this.state.values.split(",");
    values = values.map(Function.prototype.call, String.prototype.trim);
    let index = values.indexOf(val.trim());
    if (index > -1) {
      values.splice(index, 1);
    }
    this.setState({ values: values.join(", ") });
  };

  handleChange = ({ currentTarget: input }) => {
    this.setState({ newValue: input.value });
  };

  handleCreate = () => {
    let values = this.state.values + ", " + this.state.newValue;
    this.setState({ values });
  };

  //   <div className="list-group-item"> sup Menu </div>

  render() {
    return (
      <div>
        <Popup
          trigger={
            <button onClick={this.handleClick} className="btn btn-primary menu">
              Aliases
            </button>
          }
            position="bottom left"
          offsetX={0}
          offsetY={1}
          on="hover"
          closeOnDocumentClick
          mouseLeaveDelay={100}
          mouseEnterDelay={0}
          lockScroll={true}
          contentStyle={{ padding: "0px", border: "none" }}
          arrow={false}
        >
          <div className="menu aliases">
            <ul className="list-group list-group-flush">
              {this.state.values.split(",").map(val => {
                if (val.trim() === "") {
                  return null;
                }
                return (
                  <div className="input-group aliases" key={shortid.generate()}>
                    <button
                      onClick={() => {
                        this.handleDelete(val);
                      }}
                      className="btn-sm btn-danger"
                      type="button"
                    >
                      -
                    </button>
                    <li className="list-group-item list-group-item-primary aliases">
                      {val}
                    </li>
                  </div>
                );
              })}
              <div className="input-group mx-sm">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Input alias"
                  value={this.state.newValue}
                  onChange={this.handleChange}
                  // aria-label="Recipient's username"
                  // aria-describedby="basic-addon2"
                />
                <div className="input-group-append">
                  <button
                    onClick={this.handleCreate}
                    className="btn btn-success"
                    type="button"
                  >
                    Add alias
                  </button>
                </div>
              </div>
            </ul>
          </div>
        </Popup>

