import React, { Component } from "react";
import Input from "./input.jsx";

class InputList extends Component {
  state = {
    title: "spark",
    aliases: [{ id: 1, value: "apachespark" }, { id: 2, value: "apache spark" }]
  };

  handleChange = ({ currentTarget: input }) => {
    console.log("ayooo");
    const aliases = { ...this.state.aliases };
    console.log(input);
    // aliases[input.key] = input.value;
    // this.setState({ aliases });
  };

  render() {
    const curState = this.state;

    return (
      <div>
        <Input
          name="ti"
          label="title"
          value={curState.title}
          onChange={this.handleChange}
        />
        <div>
          {this.state.aliases.map((alias, i) => (
            <Input
              key={i}
              name="al"
              label="alias"
              value={alias.value}
              onChange={this.handleChange}
            />
          ))}
        </div>
        <button className="btn btn-primary">Add alias</button>
      </div>
    );
  }
}

export default InputList;
