import React, { Component } from "react";

const Input = ({ name, id, label, value, onChange, size }) => {
  return (
    <div className="form-group">
      <label className="ml-5 mt-4" htmlFor={name}>
        {label}
      </label>
      <input
        autoComplete="off"
        value={value}
        onChange={onChange}
        id={id}
        type="text"
        className={"form-control " + size}
      />
    </div>
  );
};

export default Input;
