import React, { Component } from "react";
import InputList from "./inputList.jsx";
import http from "../services/httpService";
import config from "../config.json";
import { toast } from "react-toastify";
import TitledInputList from "./titledInputList";

class ScraperForm extends Component {
  state = {
    job: { title: "", skill: "" }
  };

  componentDidMount() {}

  handlePost = async e => {
    console.log("handle post triggered");
    e.preventDefault();

    // const originalJob = this.state.job;

    // try {
    //   await http.post(
    //     config.scraperServerAPIEndpoint + "/api/scraper",
    //     this.state.job
    //   );
    // } catch (ex) {
    //   toast.error("hmmm");
    //   this.setState({ job: originalJob });
    // }
  };

  handleChange = ({ currentTarget: input }) => {
    const job = { ...this.state.job };
    job[input.name] = input.value;
    this.setState({ job });
  };

  render() {
    const job = this.state;

    return (
      <div>
        <h1>Create job</h1>
        <form onSubmit={this.handlePost}>
          <TitledInputList />
          <button className="btn btn-primary">Submit job</button>
        </form>
      </div>
    );
  }
}

export default ScraperForm;
