import React, { Component } from "react";
import Input from "./input.jsx";

class InputList extends Component {
  state = {
    title: "spark",
    aliases: {
      0: "apachespark",
      1: "apache spark"
    }
  };

  handleChange = ({ currentTarget: input }) => {
    const aliases = { ...this.state.aliases };
    aliases[input.id] = input.value;
    this.setState({ aliases });
  };

  handleDeleteAlias = (key) => {
      const aliases = {...this.state.aliases };
      delete aliases[key]
  };

  handleNewAlias = () => {
    const aliases = { ...this.state.aliases };
    aliases[Object.keys(aliases).length] = "";
    this.setState({ aliases });
  };

  render() {
    const curState = this.state;

    return (
      <div>
        <Input
          name="title"
          label="title"
          value={curState.title}
          onChange={this.handleChange}
        />
        <div>
          {Object.keys(curState.aliases).map(key => (
            <React.Fragment key={"rf" + key}>
              <Input
                key={key}
                name={key}
                label="alias"
                value={curState.aliases[key]}
                onChange={this.handleChange}
              />
              <button
                onClick={ () => this.handleDeleteAlias(key) }
                className="btn btn-danger"
              >
                -
              </button>
            </React.Fragment>
          ))}
        </div>
        <button onClick={this.handleNewAlias} className="btn btn-success">
          +
        </button>
      </div>
    );
  }
}

export default InputList;
