        1: "apache spark"
    }
  };

  handleChange = ({ currentTarget: input }) => {
    const aliases = { ...this.state.aliases };
    // console.log(input);
    console.log(aliases);
    // aliases[input] = ; 
    // console.log(aliases);
    // this.setState({ aliases });
  };

  render() {
    const curState = this.state;

    return (
      <div>
        <Input
          name="title"
          label="title"
          value={curState.title}
          onChange={this.handleChange}
        />
        <div>
          {Object.keys(curState.aliases).map(function(key) {
              <Input 
              key={key}
              name={key}
              label="alias"
              value={curState[key]}
              onChange={this.handleChange}
              />
          })

