import React, { Component } from "react";
import AliasedInput from "./aliasedInput.jsx";
import SkillInput from "./skillInput.jsx";
const shortid = require("shortid");

const SkillsGrid = ({
  skills,
  handleSkillTitleChange,
  handleSkillAliasChange,
  handleDelete,
  handleClear,
  numCols
}) => {
  let cols = [0, 1, 2];
  return (
    <div className="container">
      {cols.map( (col, skills) => {
          console.log(skills);
        return (
          <div className="row" key={shortid.generate()}>
            <div className="col-lg">
              {" "}
              {Object.keys(skills).map((key, i) => {
                if (i % 3 != col) {
                  return;
                }
                return (
                  <SkillInput
                    id={key}
                    titleLabel="Skill"
                    name={key}
                    title={skills[key].title}
                    aliases={skills[key].aliases}
                    handleTitleChange={handleSkillTitleChange}
                    handleAliasChange={handleSkillAliasChange}
                    handleDelete={handleDelete}
                  />
                );
              })}
            </div>
          </div>
        );
      })}
    </div>
  );
};

