import React, { Component } from "react";
import Input from "./input.jsx";

class InputList extends Component {
  state = {
    inputs: {
      0: ""
    }
  };

  componentDidMount() {
    this.setState({
      title: "spark",
      inputs: {
        0: "apachespark",
        1: "apache spark"
      }
    });
  }

  handleChange = ({ currentTarget: input }) => {
    const inputs = { ...this.state.inputs };
    inputs[input.id] = input.value;
    this.setState({ inputs });
  };

  handleDelete = key => {
    const inputs = { ...this.state.inputs };
    delete inputs[key];
    let updateDatedInputs = {};
    Object.values(inputs).forEach(function(value, i) {
        updateDatedAliases[i] = value;
    })
    this.setState({ inputs: updateDatedInputs });
  };

  handleCreate = () => {
    const inputs = { ...this.state.inputs };
    inputs[Object.keys(inputs).length] = "";
    this.setState({ inputs });
  };

  render() {
    const curState = this.state;

    return (
      <div>
        <div>
          {Object.keys(curState.aliases).map(key => (
            <React.Fragment key={"rf" + key}>
              <Input
                key={key}
                name={key}
                label="alias"
                value={curState.aliases[key]}
                onChange={this.handleChange}
              />
              <button
                onClick={() => this.handleDelete(key)}
                className="btn btn-danger"
              >
                -
              </button>
            </React.Fragment>
          ))}
        </div>
        <button onClick={this.handleCreate} className="btn btn-success">
          +
        </button>
      </div>
    );
  }
}

export default InputList;
