import React, { Component } from 'react';
import {
    ComposableMap,
    ZoomableGroup,
    Geographies,
    Geography,
    Markers,
    Marker,
  } from "react-simple-maps"
import { scaleLinear } from "d3-scale"
import axios from "axios"


class Map extends Component {
    constructor() {
        super()
        this.state = {
            // ComposableMap specs
            width: 800,
            height: 450,
            projection: "times",
            scale: 160,
            // ZoomableGroup specs
            zoom: 1,
            center: [0, 0],
            // Geographies specs
            geographies: "",
            // Marker specs
            rgba: "rgba(255,87,34,0.8)",
            size: "count",

        }
    }

    state = {  }


    render() { 
        return (  
            <div>
                <ComposableMap
                    projection={this.projection}
                    projectionConfig={{
                    scale: this.scale
                    }}
                    width={this.width}
                    height={this.height}
                    style={{
                    width: "100%",
                    height: "auto"
                    }}
                >
                    <ZoomableGroup>
                        <Geographies
                            geography={process.env.PUBLIC_URL + "/usa_topo.json"}
                            >

                            <Geography>
                            {(geographies, projection) =>
                                geographies.map((geography, i) => {
                                return (
                                    <Geography
                                    key={`state-${geography.properties.ID_1}`}
                                    cacheId={`state-${geography.properties.ID_1}`}
                                    round
                                    geography={geography}
                                    projection={projection}
                                    onclick={() => alert("hey")}
                                    style={{
                                        default: {
                                        stroke: "#607D8B",
                                        strokeWidth: 0.75,
                                        outline: "none"
                                        },
                                        hover: {
                                        fill: "#607D8B",
                                        stroke: "#607D8B",
                                        strokeWidth: 0.75,
                                        outline: "none"
                                        },
                                        pressed: {
                                        fill: "#FF5722",
                                        stroke: "#607D8B",
                                        strokeWidth: 0.75,
                                        outline: "none"
                                        }
                                    }}
                                    />
                                );
                                })
                            }
                            </Geography>
                        </Geographies>
                        <Markers>

                        </Markers>
                    </ZoomableGroup>
                </ComposableMap>
            </div>
        );
    }
}

export default Map;
