import React, { Component } from "react";
import AliasedInput from "./aliasedInput.jsx";
import SkillInput from "./skillInput.jsx";

const SkillsGrid = ({
  skills,
  handleSkillTitleChange,
  handleSkillAliasChange,
  handleDelete,
  handleClear,
  numCols
}) => {

  return (
    <div className="container">
      <div className="row">
        <div className="col-lg">
          {" "}
          {Object.keys(skills).map((key, i) => {
            if (i % 3 != 0) {
              return;
            }
            return (
              <SkillInput
                key={key}
                titleLabel="Skill"
                name={key}
                title={skills[key].title}
                aliases={skills[key].aliases}
                handleTitleChange={handleSkillTitleChange}
                handleAliasChange={handleSkillAliasChange}
                handleDelete={handleDelete}
              />
            );
          })}
        </div>

        <div className="col-lg">
          {" "}
          {Object.keys(skills).map((key, i) => {
            if (i % 3 != 1) {
              return;
            }
            return (
              <SkillInput
                key={key}
                titleLabel="Skill"
                name={key}
                title={skills[key].title}
                aliases={skills[key].aliases}
                handleTitleChange={handleSkillTitleChange}
                handleAliasChange={handleSkillAliasChange}
              />
            );
          })}
        </div>

        <div className="col-lg">
          {" "}
          {Object.keys(skills).map((key, i) => {
            if (i % 3 != 2) {
              return;
            }
            return (
              <SkillInput
                key={key}
                titleLabel="Skill"
                name={key}
                title={skills[key].title}
                aliases={skills[key].aliases}
                handleTitleChange={handleSkillTitleChange}
                handleAliasChange={handleSkillAliasChange}
              />
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default SkillsGrid;
