import React, { Component } from "react";
import Input from "./input.jsx";
import http from '../services/httpService';
import config from './config.json';

class ScraperForm extends Component {
  state = {
    job: { title: "", skill: "" }
  };

  handlePost = async e => {
    e.preventDefault();

    const originalJob = this.state.job

    try {
        await http.post(config.scraperServerAPIEndpoint, job)
    } catch (ex) {
        alert("hmmm");
        this.setState({ job: originalJob });
    }
  };

  handleChange = ({ currentTarget: input }) => {
    const job = { ...this.state.job };
    job[input.name] = input.value;
    this.setState({ job });
  };

  render() {
    const job = this.state;

    return (
      <div>
        <h1>Create job</h1>
        <form onSubmit={this.handlePost}>
          <Input
            name="title"
            label="Job Title"
            value={job.title}
            onChange={this.handleChange}
          />
          <Input
            name="skill"
            label="Skill"
            value={job.skill}
            onChange={this.handleChange}
          />
          <button className="btn btn-primary">Submit job</button>
        </form>
      </div>
    );
  }
}

export default ScraperForm;
