import React, { Component } from "react";
const shortid = require("shortid");

class InputList extends Component {
  state = {
    values: "react, node",
    newValue: "",
    visible: false
  };

  handleClick = () => {
    const visible = !this.state.visible;
    this.setState({ visible });
  };

  handleDelete = val => {
    let values = this.state.values.split(",");
    values = values.map(Function.prototype.call, String.prototype.trim);
    let index = values.indexOf(val.trim());
    if (index > -1) {
      values.splice(index, 1);
    }
    this.setState({ values: values.join(", ") });
  };

  handleChange = ({ currentTarget: input }) => {
    this.setState({ newValue: input.value });
  };

  handleCreate = () => {
    let values = this.state.values + ", " + this.state.newValue;
    this.setState({ values });
  };

  render() {
    return (
      <div>
        <button onClick={this.handleClick} className="btn btn-primary">
          Aliases
        </button>

        <div className="modal" tabindex="-1" role="dialog">
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Modal title</h5>
                <button
                  type="button"
                  className="close"
                  data-dismiss="modal"
                  aria-label="Close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
                <p>Modal body text goes here.</p>
              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-primary">
                  Save changes
                </button>
                <button
                  type="button"
                  className="btn btn-secondary"
                  data-dismiss="modal"
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>

