import React, { Component } from "react";
import AliasedInput from "./aliasedInput";

const SkillsGrid = ({
  skills,
  handleSkillTitleChange,
  handleSkillAliasChange,
  handleDelete,
  handleClear,
  numCols
}) => {
  return (
    <div className="container">
      <div className="row">
        <div className="col-lg">
          {" "}
          {Object.keys(skills).map((key, i) => {
            if (i % 3 != 0) {
              return;
            }
            return (
              <div key={"div-" + key}>
                <div key={key}>
                  <AliasedInput
                    titleLabel="Skill"
                    name={key}
                    title={skills[key].title}
                    aliases={skills[key].aliases}
                    handleTitleChange={handleSkillTitleChange}
                    handleAliasChange={handleSkillAliasChange}
                  />
                </div>
                <button
                  key={"btn-del" + key}
                  onClick={() => {
                    handleDelete(key);
                  }}
                  className="btn-sm btn-danger mt-0 ml-1"
                >
                  -
                </button>
                <button
                  key={"btn-clear-" + key}
                  onClick={() => handleClear(key)}
                  className="btn-sm btn-warning mt-3 ml-2"
                >
                  Clear
                </button>
              </div>
            );
          })}
        </div>

        <div className="col-lg">
          {" "}
          {Object.keys(skills).map((key, i) => {
            if (i % 3 != 1) {
              return;
            }
            return (
              <div key={"div-" + key}>
                <div key={key}>
                  <AliasedInput
                    titleLabel="Skill"
                    name={key}
                    title={skills[key].title}
                    aliases={skills[key].aliases}
                    handleTitleChange={handleSkillTitleChange}
                    handleAliasChange={handleSkillAliasChange}
                  />
                </div>
                <button
                  key={"btn-del" + key}
                  onClick={() => {
                    handleDelete(key);
                  }}
                  className="btn-sm btn-danger mt-0 ml-1"
                >
                  -
                </button>
                <button
                  key={"btn-clear-" + key}
                  onClick={() => handleClear(key)}
                  className="btn-sm btn-warning mt-3 ml-2"
                >
                  Clear
                </button>
              </div>
            );
          })}
        </div>

        <div className="col-lg">
          {" "}
          {Object.keys(skills).map((key, i) => {
            if (i % 3 != 2) {
              return;
            }
            return (
              <div key={"div-" + key}>
                <div key={key}>
                  <AliasedInput
                    titleLabel="Skill"
                    name={key}
                    title={skills[key].title}
                    aliases={skills[key].aliases}
                    handleTitleChange={handleSkillTitleChange}
                    handleAliasChange={handleSkillAliasChange}
                  />
                </div>
                <button
                  key={"btn-del" + key}
                  onClick={() => {
                    handleDelete(key);
                  }}
                  className="btn-sm btn-danger mt-0 ml-1"
                >
                  -
                </button>
                <button
                  key={"btn-clear-" + key}
                  onClick={() => handleClear(key)}
                  className="btn-sm btn-warning mt-3 ml-2"
                >
                  Clear
                </button>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default SkillsGrid;
